Script.Workflow:

module viewmodel;

using system::*;
using presentation::*;

namespace Smbios
{
  class SmbiosTypaDataItem
  {
    prop Desc : string = "" {not observe}
    prop Type : int = 0 {}
    prop Address : Int64 = 0 {}
    new()
    {
    }
    new(smbios_type : int, desc : string, addr : Int64)
    {
      Desc = desc;
      Type = smbios_type;
      Address = addr;
    }
  }

 class DisplayDataToSmbiosData
  {
    prop Desc : string = "" {not observe}
    prop Display : string = "" {not observe}
    prop SmbiosType : int = 0 {}
    prop SmbiosTypeValue : string = "" {not observe}
    new()
    {
    }
    new(desc : string, display : string, smbios_type : int, value : string)
    {
      Desc = desc;
      Display = display;
      SmbiosType = smbios_type;
      SmbiosTypeValue = value;
    }
  }
}
