Script.Workflow:

module viewmodel;

using system::*;
using presentation::*;

namespace Smbios
{
  class SmbiosTypaDataItem
  {
    prop Desc : string = "" {not observe}
    prop Type : int = 0 {not observe}
    prop Address : Int64 = 0 {not observe}
    new()
    {
    }
    new(desc : string)
    {
      Desc = desc;
      Type = 0;
      Address = 0;
    }
  }

 class DisplayDataToSmbiosData
  {
    prop Desc : string = "" {not observe}
    prop Display : string = "" {not observe}
    prop SmbiosType : int = 0 {not observe}
    prop SmbiosTypeValue : string = "" {not observe}
    new()
    {
    }
    new(desc : string, display : string, value : string)
    {
      Desc = desc;
      Display = display;
      SmbiosType = 0;
      SmbiosTypeValue = value;
    }
  }
}
